
public class Ex03_05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.printf("%d\n", 123);
		System.out.printf("5%d\n", 123);
		System.out.printf("0%d\n\n", 123);

		System.out.printf("%f\n", 123.45);
		System.out.printf("%7.1f\n", 123.45); // 소수점 둘째 자리에서 반올림 4=> X 5 => O   (123.45) => (123.5)
		System.out.printf("%7.3f\n\n", 123.45);

		System.out.printf("%s\n", "Hi~Java");
		System.out.printf("%10s\n", "Hi~Java");

	}

}
