
public class Ex03_07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		float b;
		a = (int) 123.45f;          // (int) 123.45f ==> float값을 int로 변환
		b = 200;
		
		System.out.printf("a ==> %d\n", a);
		System.out.printf("b ==> %f", b);

	}

}
